pragma solidity ^0.5.8;
import "./TCRDevToken.sol";
import "./tcr-dependencies/DLL.sol";
import "./tcr-dependencies/AttributeStore.sol";

// Partial-Lock-Majority Voting. Which is non-incentivized voting that can
// be seen on the blockchain. Because there are no rewards for voting the right
// way, this is OKAY
contract PLMVoting {

    // ============
    // EVENTS:
    // ============

    event Vote(
        address indexed voter,
        uint indexed pollID,
        uint indexed numTokens,
        uint choice
    );
    event PollCreated(
        uint indexed voteQuorum,
        uint voteDuration,
        uint indexed pollID
    );
    event VotingRightsGranted(address indexed voter, uint indexed numTokens);
    event VotingRightsWithdrawn(address indexed voter, uint indexed numTokens);

    // ============
    // DATA STRUCTURES:
    // ============

    using AttributeStore for AttributeStore.Data;
    using DLL for DLL.Data;

    struct Poll {
        uint voteQuorum;        /// number of votes required for a proposal to pass
        uint votingEndDate;     /// expiration date of commit period for poll
        uint votesFor;          /// tally of votes supporting proposal
        uint votesAgainst;      /// tally of votes countering proposal
    }

    // ============
    // STATE VARIABLES:
    // ============

    PLMVoting masterCopy; // THIS MUST ALWAYS BE THE FIRST STATE VARIABLE DECLARED!!!!!!

    uint constant public INITIAL_POLL_NONCE = 0;
    uint public pollNonce;

    mapping(uint => Poll) public pollMap; // maps pollID to Poll struct
    mapping(address => uint) public voteTokenBalance; // maps user's address to voteToken balance

    mapping(address => DLL.Data) dllMap;
    AttributeStore.Data store;

    TCRDevToken public token;

    // ============
    // CONSTRUCTOR:
    // ============

    /**
    @dev uses the setup function to initialize PLM by specifying the token used for voting
    @param _tokenAddr The address of the ERC20 token to be used for voting
    */
    constructor (address _tokenAddr) public {
        setup(_tokenAddr);
    }

    /**
    @dev initializes the contract by spcifying the token used for voting. Can be called by proxy
    contracts to initialize their state.
    @param _tokenAddr The address of the ERC20 token to be used for voting
    */
    function setup(address _tokenAddr) public {
        require(address(token) == address(0), "Token address can't be set to 0 address");

        token = TCRDevToken(_tokenAddr);
        pollNonce = INITIAL_POLL_NONCE;
    }

    // ================
    // TOKEN INTERFACE:
    // ================

    /**
    @notice Loads _numTokens ERC20 tokens into the voting contract for one-to-one voting rights
    @dev Assumes that msg.sender has approved voting contract to spend on their behalf
    @param _numTokens The number of votingTokens desired in exchange for ERC20 tokens
    */
    function requestVotingRights(uint _numTokens) external {
        require(token.balanceOf(msg.sender) >= _numTokens, "msg.sender doesn't have enough tokens");
        require(token.transferFrom(msg.sender, address(this), _numTokens), "Transfer failed");
        voteTokenBalance[msg.sender] += _numTokens;
        emit VotingRightsGranted(msg.sender, _numTokens);
    }

    /**
    @notice Withdraw _numTokens ERC20 tokens from the voting contract, revoking these voting rights
    @param _numTokens The number of ERC20 tokens desired in exchange for voting rights
    */
    function withdrawVotingRights(uint _numTokens) public {
        uint availableTokens = voteTokenBalance[msg.sender] - getLockedTokens(msg.sender);
        require(availableTokens >= _numTokens, "User does not have enough tokens to withdraw");
        require(token.transfer(msg.sender, _numTokens), "Token transfer failed");
        voteTokenBalance[msg.sender] -= _numTokens;
        emit VotingRightsWithdrawn(msg.sender, _numTokens);
    }

    /**
    @dev Unlocks tokens locked in vote where poll has ended
    @param _pollID Integer identifier associated with the target poll
    */
    function rescueTokens(uint _pollID) public {
        require(pollEnded(_pollID), "Poll has not ended yet");
        dllMap[msg.sender].remove(_pollID);
    }

    /**
    @dev Rescue tokens from old polls, and then withdraw the designated amount of tokens. This
         should be called so that the user can withdraw with one transaction
    */
    function rescueAndWithdraw(uint[] calldata _pollIDs, uint _numTokens) external {
        for(uint256 i; i < _pollIDs.length; i++){
            rescueTokens(_pollIDs[i]);
        }
        withdrawVotingRights(_numTokens);
    }


    // =================
    // VOTING INTERFACE:
    // =================

    /**
    @notice Commits vote
    @param _pollID      Integer identifier associated with target poll
    @param _numTokens   The number of tokens to be committed towards the target poll
    @param _prevPollID  The ID of the poll that the user has voted the maximum number of tokens
                        in which is still less than or equal to numTokens
    @param _voteOption  True if voting yes, false if voting no
    */
    function vote(
        uint _pollID,
        uint _numTokens,
        uint _prevPollID,
        uint _voteOption

    ) external {
        require(votingPeriodActive(_pollID), "The voting period is not active");
        require(voteTokenBalance[msg.sender] >= _numTokens, "User does not have enough tokens");
        require(_pollID != 0, "Can't commit to 0 node");

        // TODO: Move all insert validation into the DLL lib
        // Check if _prevPollID exists
        // TODO DK - can't this be 0 if smeone skipped the last poll?
        // or is this requiring you insert your own previous poll? (I think this is the case)
        require(
            _prevPollID == 0 || getVoteChoice(msg.sender, _prevPollID) != 0,
            "Previous poll ID does not exist"
        );

        uint nextPollID = dllMap[msg.sender].getNext(_prevPollID);

        // if nextPollID is equal to _pollID, _pollID is being updated,
        nextPollID = (nextPollID == _pollID) ? dllMap[msg.sender].getNext(_pollID) : nextPollID;

        require(
            validPosition(_prevPollID, nextPollID, msg.sender, _numTokens),
            "Polls were not sorted properly"
        );
        dllMap[msg.sender].insert(_prevPollID, _pollID, nextPollID);

        bytes32 UUID = attrUUID(msg.sender, _pollID);

        store.setAttribute(UUID, "numTokens", _numTokens);
        store.setAttribute(UUID, "voteChoice", _voteOption);

        // 1 if voting yes, all other numbers if voting no
        if (_voteOption == 1) // apply numTokens to appropriate poll choice
            pollMap[_pollID].votesFor += _numTokens;
        else
            pollMap[_pollID].votesAgainst += _numTokens;

        emit Vote(msg.sender, _pollID, _numTokens, _voteOption);
    }

    /**
    @dev Compares previous and next poll's committed tokens for sorting purposes
    @param _prevID Integer identifier associated with previous poll in sorted order
    @param _nextID Integer identifier associated with next poll in sorted order
    @param _voter Address of user to check DLL position for
    @param _numTokens The number of tokens to be committed towards the poll (used for sorting)
    @return valid Boolean indication of if the specified position maintains the sort
    */
    function validPosition(
        uint _prevID,
        uint _nextID,
        address _voter,
        uint _numTokens
    ) public view returns (bool valid) {
        bool prevValid = (_numTokens >= getNumTokens(_voter, _prevID));
        // if next is zero node, _numTokens does not need to be greater
        bool nextValid = (_numTokens <= getNumTokens(_voter, _nextID) || _nextID == 0);
        return prevValid && nextValid;
    }


    /**
    @param _pollID Integer identifier associated with target poll
    @return correctVotes Number of tokens voted for winning option
    */
    function getNumPassingTokens(
        address _voter,
        uint _pollID
    ) public view returns (uint correctVotes) {
        require(pollEnded(_pollID), "Poll has not ended");

        uint winningChoice = isPassed(_pollID) ? 1 : 0;
        uint voteChoice = getVoteChoice(_voter, _pollID);

        require(winningChoice == voteChoice, "User choice does not match winning choice");

        return getNumTokens(_voter, _pollID);
    }

    // ==================
    // POLLING INTERFACE:
    // ==================

    /**
    @dev Initiates a poll with canonical configured parameters at pollID emitted by PollCreated event
    @param _voteQuorum Type of majority (out of 100) that is necessary for poll to be successful
    @param _votingDuration Length of voting duration for the challenge poll
    */
    function startPoll(
        uint _voteQuorum,
        uint _votingDuration
    ) public returns (uint pollID) {
        pollNonce = pollNonce + 1;

        pollMap[pollNonce] = Poll({
            voteQuorum: _voteQuorum,
            /* solium-disable-next-line security/no-block-members*/
            votingEndDate: block.timestamp + _votingDuration,
            votesFor: 0,
            votesAgainst: 0
        });

        emit PollCreated(_voteQuorum, _votingDuration, pollNonce);
        return pollNonce;
    }

    /**
    @notice Determines if proposal has passed
    @dev Check if votesFor out of totalVotes exceeds votesQuorum (requires pollEnded)
    @param _pollID Integer identifier associated with target poll
    */
    function isPassed(uint _pollID) public view returns (bool passed) {
        require(pollEnded(_pollID), "Poll has not ended");

        Poll memory poll = pollMap[_pollID];
        return (100 * poll.votesFor) > (poll.voteQuorum * (poll.votesFor + poll.votesAgainst));
    }

    // ----------------
    // POLLING HELPERS:
    // ----------------

    /**
    @dev Gets the bytes32 voteChoice property of target poll
    @param _voter Address of user to check against
    @param _pollID Integer identifier associated with target poll
    @return uint vote choice attached to target poll
    */
    function getVoteChoice(address _voter, uint _pollID) public view returns (uint voteChoice) {
        return store.getAttribute(attrUUID(_voter, _pollID), "voteChoice");
    }

    /**
    @dev Gets the total winning votes for reward distribution purposes
    @param _pollID Integer identifier associated with target poll
    @return Total number of votes committed to the winning option for specified poll
    */
    function getTotalNumberOfTokensForWinningOption(
        uint _pollID
    ) public view returns (uint numTokens) {
        require(pollEnded(_pollID), "Poll has not ended");

        if (isPassed(_pollID))
            return pollMap[_pollID].votesFor;
        else
            return pollMap[_pollID].votesAgainst;
    }

    /**
    @notice Determines if poll is over
    @dev Checks isExpired for specified poll's revealEndDate
    @return Boolean indication of whether polling period is over
    */
    function pollEnded(uint _pollID) public view returns (bool ended) {
        require(pollExists(_pollID), "Poll does not exist");

        return isExpired(pollMap[_pollID].votingEndDate);
    }

    /**
    @notice Checks if the commit period is still active for the specified poll
    @dev Checks isExpired for the specified poll's commitEndDate
    @param _pollID Integer identifier associated with target poll
    @return Boolean indication of isCommitPeriodActive for target poll
    */
    function votingPeriodActive(uint _pollID) public view returns (bool active) {
        require(pollExists(_pollID), "Poll does not exist");

        return !isExpired(pollMap[_pollID].votingEndDate);
    }

    /**
    @dev Checks if a poll exists, throws if the provided poll is in an impossible state
    @param _pollID The pollID whose existance is to be evaluated.
    @return Boolean Indicates whether a poll exists for the provided pollID
    */
    function pollExists(uint _pollID) public view returns (bool exists) {
        uint votingEndDate = pollMap[_pollID].votingEndDate;
        if (votingEndDate == 0)
            return false;
        return true;
    }

    // ---------------------------
    // DOUBLE-LINKED-LIST HELPERS:
    // ---------------------------

    /**
    @dev Wrapper for getAttribute with attrName="numTokens"
    @param _voter Address of user to check against
    @param _pollID Integer identifier associated with target poll
    @return Number of tokens committed to poll in sorted poll-linked-list
    */
    function getNumTokens(address _voter, uint _pollID) public view returns (uint numTokens) {
        return store.getAttribute(attrUUID(_voter, _pollID), "numTokens");
    }

    /**
    @dev Gets top element of sorted poll-linked-list
    @param _voter Address of user to check against
    @return Integer identifier to poll with maximum number of tokens committed to it
    */
    function getLastNode(address _voter) public view returns (uint pollID) {
        return dllMap[_voter].getPrev(0);
    }

    /**
    @dev Gets the numTokens property of getLastNode
    @param _voter Address of user to check against
    @return Maximum number of tokens committed in poll specified
    */
    function getLockedTokens(address _voter) public view returns (uint numTokens) {
        return getNumTokens(_voter, getLastNode(_voter));
    }

    /**
    @dev Gets the prevNode a new node should be inserted after given the sort factor
    @param _voter The voter whose DLL will be searched
    @param _numTokens The value for the numTokens attribute in the node to be inserted
    @return the node which the propoded node should be inserted after
    */
    function getInsertPointForNumTokens(address _voter, uint _numTokens)
    public view returns (uint prevNode) {
        uint nodeID = getLastNode(_voter);
        uint tokensInNode = getNumTokens(_voter, nodeID);

        while(tokensInNode != 0) {
            tokensInNode = getNumTokens(_voter, nodeID);
            if(tokensInNode < _numTokens) {
                return nodeID;
            }
            nodeID = dllMap[_voter].getPrev(nodeID);
        }

        return nodeID;
    }

    // ----------------
    // GENERAL HELPERS:
    // ----------------

    /**
    @dev Checks if an expiration date has been reached
    @param _terminationDate Integer timestamp of date to compare current timestamp with
    @return expired Boolean indication of whether the terminationDate has passed
    */
    function isExpired(uint _terminationDate) public view returns (bool expired) {
        /* solium-disable-next-line security/no-block-members*/
        return (block.timestamp > _terminationDate);
    }

    /**
    @dev Generates an identifier which associates a user and a poll together
    @param _pollID Integer identifier associated with target poll
    @return UUID Hash which is deterministic from _user and _pollID
    */
    function attrUUID(address _user, uint _pollID) public pure returns (bytes32 UUID) {
        return keccak256(abi.encodePacked(_user, _pollID));
    }
}

