pragma solidity ^0.5.8;

import "./PLMVoting.sol";
import "./TCRDevToken.sol";

contract Parameterizer {
    // ------
    // EVENTS
    // ------

    event _ReparameterizationProposal(
        address indexed proposer,
        string name,
        uint256 indexed value,
        bytes32 indexed propID
    );
    event _NewChallenge(
        address indexed challenger,
        bytes32 indexed propID,
        uint256 indexed pollID
    );

    // ------
    // DATA STRUCTURES
    // ------

    struct ParamProposal {
        uint256 appExpiry;
        uint256 challengeID;
        uint256 deposit;
        string name;
        address owner;
        uint256 processBy;
        uint256 value;
    }

    struct Challenge {
        uint256 rewardPool; // (remaining) pool of tokens distributed amongst winning voters
        address challenger; // owner of Challenge
        bool resolved; // indication of if challenge is resolved
        uint256 stake; // number of tokens at risk for either party during challenge
        uint256 winningTokens; // (remaining) amount of tokens used for voting by the winning side
        mapping(address => bool) voterCanClaimReward;
    }

    // ------
    // STATE
    // ------

    Parameterizer masterCopy; // THIS MUST ALWAYS BE THE FIRST STATE VARIABLE DECLARED!!!!!!

    mapping(bytes32 => uint256) public params;

    // maps challengeIDs to associated challenge data
    mapping(uint256 => Challenge) public challenges;

    // maps pollIDs to intended data change if poll passes
    mapping(bytes32 => ParamProposal) public proposals;

    // Global Variables
    TCRDevToken public token;
    PLMVoting public voting;
    uint256 public PROCESSBY = 604800; // 7 days

    // ------------
    // CONSTRUCTOR
    // ------------

    /**
  @dev constructor
  @param _tokenAddr         address of the token which parameterizes this system
  @param _plmAddr           address of a PLM voting contract for the provided token
  @param _requiredDeposit   required deposit for listing to be whitelisted
  @param _pRequiredDeposit  required deposit to propose a reparameterization
  @param _pApplyStageLen    period over which reparmeterization proposal's wait to be processed
  @param _dispensationPct   % of losing party's deposit sent to winning party
  @param _pDispensationPct  % of losing party's deposit sent to winning party in parameterizer
  @param _voteStageLen      length of commit period for voting
  @param _pVoteStageLen     length of commit period for voting in parameterizer
  @param _voteQuorum        type of majority out of 100 necessary for vote success
  @param _pVoteQuorum       type of majority out of 100 necessary for vote success in parameterizer
  */
    constructor(
        address _tokenAddr,
        address _plmAddr,
        uint256 _requiredDeposit,
        uint256 _pRequiredDeposit,
        uint256 _pApplyStageLen,
        uint256 _dispensationPct,
        uint256 _pDispensationPct,
        uint256 _voteStageLen,
        uint256 _pVoteStageLen,
        uint256 _voteQuorum,
        uint256 _pVoteQuorum
    ) public {
        setup(
            _tokenAddr,
            _plmAddr,
            _requiredDeposit,
            _pRequiredDeposit,
            _pApplyStageLen,
            _dispensationPct,
            _pDispensationPct,
            _voteStageLen,
            _pVoteStageLen,
            _voteQuorum,
            _pVoteQuorum
        );
    }

    function setup(
        address _tokenAddr,
        address _plmAddr,
        uint256 _requiredDeposit,
        uint256 _pRequiredDeposit,
        uint256 _pApplyStageLen,
        uint256 _dispensationPct,
        uint256 _pDispensationPct,
        uint256 _voteStageLen,
        uint256 _pVoteStageLen,
        uint256 _voteQuorum,
        uint256 _pVoteQuorum
    ) public {
        require(address(token) == address(0), "Token address can't be the 0 address");

        token = TCRDevToken(_tokenAddr);
        voting = PLMVoting(_plmAddr);

        set("requiredDeposit", _requiredDeposit);
        set("pRequiredDeposit", _pRequiredDeposit);
        set("pApplyStageLen", _pApplyStageLen);
        set("dispensationPct", _dispensationPct);
        set("pDispensationPct", _pDispensationPct);
        set("voteStageLen", _voteStageLen);
        set("pVoteStageLen", _pVoteStageLen);
        set("voteQuorum", _voteQuorum);
        set("pVoteQuorum", _pVoteQuorum);
    }

    // -----------------------
    // TOKEN HOLDER INTERFACE
    // -----------------------

    /**
  @notice propose a reparamaterization of the key _name's value to _value.
  @param _name the name of the proposed param to be set
  @param _value the proposed value to set the param to be set
  */
    function proposeReparameterization(string memory _name, uint256 _value)
        public
        returns (bytes32)
    {
        uint256 deposit = get("pRequiredDeposit");
        bytes32 propID = keccak256(abi.encodePacked(_name, _value));

        if (keccak256(abi.encodePacked(_name)) == keccak256(abi.encodePacked("dispensationPct")) ||
            keccak256(abi.encodePacked(_name)) == keccak256(abi.encodePacked("pDispensationPct"))) {
            require(_value <= 100, "This value must be within 100%");
        }

        require(!propExists(propID), "Proposal already exists");
        require(get(_name) != _value, "The proposed change isn't different from the current value");
        // escrow tokens (deposit amt)
        require(token.transferFrom(msg.sender, address(this), deposit), "Token transfer failed");
        // attach name and value to pollID
        proposals[propID] = ParamProposal({
            /* solium-disable-next-line security/no-block-members*/
            appExpiry: block.timestamp + get("pApplyStageLen"),
            challengeID: 0,
            deposit: deposit,
            name: _name,
            owner: msg.sender,
            /* solium-disable-next-line security/no-block-members*/
            processBy: block.timestamp +
                get("pApplyStageLen") +
                get("pVoteStageLen") +
                PROCESSBY,
            value: _value
        });

        emit _ReparameterizationProposal(msg.sender, _name, _value, propID);
        return propID;
    }

    /**
  @notice challenge the provided proposal ID, and put tokens at stake to do so.
  @param _propID the proposal ID to challenge
  */
    function challengeReparameterization(bytes32 _propID)
        public
        returns (uint256 challengeID)
    {
        ParamProposal memory prop = proposals[_propID];
        uint256 deposit = get("pRequiredDeposit");

        require(propExists(_propID) && prop.challengeID == 0, "Proposal does not exist");

        //take tokens from challenger
        require(token.transferFrom(msg.sender, address(this), deposit), "Token transfer failed");
        //start poll
        uint256 pollID = voting.startPoll(
            get("pVoteQuorum"),
            get("pVoteStageLen")
        );

        challenges[pollID] = Challenge({
            challenger: msg.sender,
            rewardPool: ((100 - get("pDispensationPct")) * deposit) / 100,
            stake: deposit,
            resolved: false,
            winningTokens: 0
        });

        proposals[_propID].challengeID = pollID; // update listing to store most recent challenge

        emit _NewChallenge(msg.sender, _propID, pollID);
        return pollID;
    }

    /**
  @notice for the provided proposal ID, set it, resolve its challenge, or delete it depending
          on whether it can be set, has a challenge which can be resolved, or if its "process by"
          date has passed
  @param _propID the proposal ID to make a determination and state transition for
  */
    function processProposal(bytes32 _propID) public {
        ParamProposal storage prop = proposals[_propID];

        if (canBeSet(_propID)) {
            set(prop.name, prop.value);
        } else if (challengeCanBeResolved(_propID)) {
            resolveChallenge(_propID);
        /* solium-disable-next-line security/no-block-members*/
        } else if (block.timestamp > prop.processBy) {
            require(token.transfer(prop.owner, prop.deposit), "Token transfer failed");
        } else {
            revert("Proposal could not be processed");
        }

        delete proposals[_propID];
    }

    /**
  @notice claim the tokens owed for the msg.sender in the provided challenge
  @param _challengeID the challenge ID to claim tokens for
  */
    function claimVoterReward(uint256 _challengeID) public {
        // ensure voter has not already claimed tokens and challenge results have been processed
        require(
            challenges[_challengeID].voterCanClaimReward[msg.sender] == false,
            "Voter can't claim reward right now"
        );
        require(challenges[_challengeID].resolved == true, "Challenge is not yet resolved");

        uint256 voterTokens = voting.getNumPassingTokens(msg.sender, _challengeID);
        uint256 reward = voterReward(msg.sender, _challengeID);

        // subtract voter's information to preserve the participation ratios of other voters
        // compared to the remaining pool of rewards
        challenges[_challengeID].winningTokens -= voterTokens;
        challenges[_challengeID].rewardPool -= reward;

        require(token.transfer(msg.sender, reward), "Token transfer failed");

        // ensures a voter cannot claim tokens again
        challenges[_challengeID].voterCanClaimReward[msg.sender] = true;
    }

    // --------
    // GETTERS
    // --------

    /**
  @dev                Calculates the provided voter's token reward for the given poll.
  @param _voter       The address of the voter whose reward balance is to be returned
  @param _challengeID The ID of the challenge the voter's reward is being calculated for
  @return             The uint indicating the voter's reward
  */
    function voterReward(address _voter, uint256 _challengeID)
        public
        view
        returns (uint256)
    {
        uint256 winningTokens = challenges[_challengeID].winningTokens;
        uint256 rewardPool = challenges[_challengeID].rewardPool;
        uint256 voterTokens = voting.getNumPassingTokens(_voter, _challengeID);
        return (voterTokens * rewardPool) / winningTokens;
    }

    /**
  @notice Determines whether a proposal passed its application stage without a challenge
  @param _propID The proposal ID to determine if the application stage passed without a challenge
  */
    function canBeSet(bytes32 _propID) public view returns (bool) {
        ParamProposal memory prop = proposals[_propID];

        return (
            /* solium-disable-next-line security/no-block-members*/
            block.timestamp > prop.appExpiry && block.timestamp < prop.processBy &&
            prop.challengeID == 0
        );
    }

    /**
  @notice Determines whether a proposal exists for the provided proposal ID
  @param _propID The proposal ID whose existance is to be determined
  */
    function propExists(bytes32 _propID) public view returns (bool) {
        return proposals[_propID].processBy > 0;
    }

    /**
  @notice Determines whether the provided proposal ID has a challenge which can be resolved
  @param _propID The proposal ID whose challenge to inspect
  */
    function challengeCanBeResolved(bytes32 _propID) public view returns (bool) {
        ParamProposal memory prop = proposals[_propID];
        Challenge memory challenge = challenges[prop.challengeID];

        return (prop.challengeID > 0 &&
            challenge.resolved == false &&
            voting.pollEnded(prop.challengeID));
    }

    /**
  @notice Determines the number of tokens to be awarded to the winning party in a challenge
  @param _challengeID The challengeID to determine a reward for
  */
    function challengeWinnerReward(uint256 _challengeID) public view returns (uint256) {
        if (voting.getTotalNumberOfTokensForWinningOption(_challengeID) == 0) {
            // Edge case, nobody voted, give all tokens to the winner.
            return 2 * challenges[_challengeID].stake;
        }

        return (2 * challenges[_challengeID].stake) - challenges[_challengeID].rewardPool;
    }

    /**
  @notice gets the parameter keyed by the provided name value from the params mapping
  @param _name the key whose value is to be determined
  */
    function get(string memory _name) public view returns (uint256 value) {
        return params[keccak256(abi.encodePacked(_name))];
    }

    // ----------------
    // PRIVATE FUNCTIONS
    // ----------------

    /**
  @dev resolves a challenge for the provided _propID. It must be checked in
       advance whether the _propID has a challenge on it
  @param _propID the proposal ID whose challenge is to be resolved.
  */
    function resolveChallenge(bytes32 _propID) private {
        ParamProposal memory prop = proposals[_propID];
        Challenge storage challenge = challenges[prop.challengeID];

        // winner gets back their full staked deposit, and dispensationPct*loser's stake
        uint256 reward = challengeWinnerReward(prop.challengeID);

        if (voting.isPassed(prop.challengeID)) {
            // The challenge failed
            /* solium-disable-next-line security/no-block-members*/
            if (prop.processBy > block.timestamp) {
                set(prop.name, prop.value);
            }
            require(token.transfer(prop.owner, reward), "Token transfer failed on failed challenge");
        } else {
            // The challenge succeeded
            require(
                token.transfer(challenges[prop.challengeID].challenger, reward),
                "Token transfer failed on successful challenge"
            );
        }

        challenge.winningTokens = voting.getTotalNumberOfTokensForWinningOption(
            prop.challengeID
        );
        challenge.resolved = true;
    }

    /**
  @dev sets the param keyed by the provided name to the provided value
  @param _name the name of the param to be set
  @param _value the value to set the param to be set
  */
    function set(string memory _name, uint256 _value) private {
        params[keccak256(abi.encodePacked(_name))] = _value;
    }
}

